INSERT INTO `tb_markers_data` (`ID`, `CID`, `LANGID`, `VALUE`, `NAME`, `URL`, `TITLE`, `KW`, `DESC`, `TEXT`, `STEXT`, `ADDRESS`) VALUES
(1, 1, 13, '', 'Stadions "Daugava"', '', '', '', '', 'Daugavas stadions ir daudzfunkcionāls stadions Rīgā, Latvijā, kas tika dibināts 1927.gadā un tika piešķirts sporta organizācijai „Strādnieku sports un sargs”. ', '', ''),
(2, 1, 11, '', 'Стадион "Даугава"', '', '', '', '', 'Daugavas stadions ir daudzfunkcionāls stadions Rīgā, Latvijā, kas tika dibināts 1927.gadā un tika piešķirts sporta organizācijai „Strādnieku sports un sargs”.', '', ''),
(3, 1, 8, '', 'Stadium "Daugava"', '', '', '', '', 'Daugavas stadions ir daudzfunkcionāls stadions Rīgā, Latvijā, kas tika dibināts 1927.gadā un tika piešķirts sporta organizācijai „Strādnieku sports un sargs”. ', '', ''),
(7, 3, 13, '', 'Grīziņkalna parks', '', '', '', '', 'Grīziņkalna parks ir parks Rīgā, Latgales priekšpilsētā, 10,45 hektāru platībā, kuru ierobežo Pērnavas, Ata un Jāņa Asara iela.\r\nParkā aug 16 vietējās un 62 introducētās koku un krūmu sugas — Ledebūra lapegle, kalnu un melnā priede, Darmštates priedes, arī papīra bērzs, Tatārijas kļava un citi.', '', ''),
(8, 3, 11, '', 'Гри́зинькалнс', '', '', '', '', ' Наименование района происходит от названия здешней песчаной дюны . В архитектурном отношении Гризинькалнс — классическое рабочее предместье, граничащее с буржуазным центром. Здесь сохранилось десятки памятников архитектуры начала XX столетия, построенные в «национальном югендстиле». Район находится в границах охранной зоны ЮНЕСКО — это своего рода «буферная зона», прилегающая к историческому центру Риги.', '', ''),
(9, 3, 8, '', 'Grizinkalns area', '', '', '', '', 'Architecturally, Grizinkalns is a classical working suburb, bordering the bourgeois center. Dozens of monuments of architecture of the beginning of the XX century, built in the "national art nouveau style", have been preserved here. The area is located within the boundaries of the UNESCO protection zone - it is a kind of "buffer zone" adjacent to the historic center of Riga.', '', ''),
(4, 2, 13, '', 'J.Stradiņa universitātes medicīnas koledža', '', '', '', '', 'Rīgas Stradiņa universitātes Sarkanā Krusta medicīnas koledža (RSU SKMK) ir pirmā līmeņa profesionālās augstākās izglītības, arodizglītības un profesionālās vidējās izglītības iestāde, kas dibināta 1920. gadā kā Latvijas Sarkanā Krusta slimnīcas žēlsirdīgo māsu skola.', '', ''),
(5, 2, 11, '', 'Медицинский колледж Красного Креста Рижского университета имени Страдыня', '', '', '', '', 'Обучение медсестёр, сестра, лечение, помощник врача, высшее образование,\r\nмедицинский колледж, сиделка, няня, домоводство, профессиональное образование,\r\nгде учиться, медицинские сестры, студии, колледж, высшая школа.', '', ''),
(6, 2, 8, '', 'Medical College of the Red Cross of the Stradina University of Riga', '', '', '', '', 'Nurse training, sister, treatment, assistant doctor, higher education,\r\nMedical college, nurse, nanny, economics, vocational education,\r\nWhere to study, nurses, studios, college, high school.', '', ''),
(13, 5, 13, '', 'Ziedoņārzs', '', '', '', '', 'Dārzs ierīkots laikposmā starp 1936. un 1939. gadu praktiski pilsētas nomalē, nabadzīgo strādnieku kvartālu vidū, kad parka atrašanās rajonā 1935. gadā tika nojaukta vecā koka apbūve. Parka nosaukumu devis tā projekta autors un toreizējais uzņēmuma "Rīgas dārzi" vadītājs Andrejs Zeidaks (1874.-1964.). Dārza ierīkošana 1939. gadā izmaksājusi 115 555 latus.\r\n', '', ''),
(14, 5, 11, '', 'Зиедоньдарзс', '', '', '', '', 'Парк Зиедоньдарзс, расположенный на стыке трёх городских районов (центр, Авоты, Гризинькалнс), фактически принадлежит всем трём районам. Он был разбит в 1937—1939 годах по проекту Андрея Зейдакса. Ему же принадлежит идея назвать новый парк Зиедоньдарзс, что означает — «цветущий сад», «весенний сад».', '', ''),
(15, 5, 8, '', 'Ziedondarzs ', '', '', '', '', 'The Ziedon Park, located at the junction of the three urban areas (center, Avotu street, Grizinkalns), actually belongs to all three districts. He was defeated in 1937-1939 under the project of Andrei Zeidaks. He also owns the idea of renaming the new Ziedondarzs, which means "a flowering garden", a "spring garden".', '', ''),
(16, 6, 13, '', 'Rīgas 6 vidusskola', '', '', '', '', 'Aleksandra Čaka iela 102, Rīga, LV-1011\r\ntālrunis un fakss lietvedībā 67314042\r\ntālrunis mācību daļā 67314244\r\ndirektora tālrunis 67314044\r\nsociālā pedagoga un psihologa tālrunis 67290477', '', ''),
